/*jshint strict:true node:true es5:true onevar:true laxcomma:true laxbreak:true eqeqeq:true immed:true latedef:true*/
(function () {
  "use strict";

  var Free = require('./')
    , fs = require('fs')
    ;

  // ['total', 'used', 'free', 'shared', 'buffers', 'cached']

  Free.free(function (err, mstat) {
    var free = mstat.total - (mstat.used - (mstat.buffers + mstat.cached))
      ;

    console.log(
        'total', mstat.total
      , '\nused', mstat.total - free
      , '\nfree', free
      , '\n% free', ((free / mstat.total) * 100).toFixed(2), '%'
    );
  });
}());
